import React, { useState } from 'react'

function App() {

  const [inputValue, setInputValue] = useState('')
  const [items, setItems] = useState([])


  const addItem = () => {
    if (inputValue != '') {
      setItems([...items, inputValue]);
      setInputValue('');
    }
  }

const delItem=(id)=>{
  const updatedItems=items.filter((elem,index)=>{
    return (index != id);
  })
  setItems(updatedItems)
}

  return (
    <>
      <div className="main">
        <div className="main1">
          <div className="main11">
            <h1 className="heading">Todo List</h1>
            <div className="inputBar">
              <input type="text" placeholder='Add Item' onChange={(e) => { setInputValue(e.target.value) }} value={inputValue} />
              <button className="btn" onClick={addItem}>+</button>
            </div>

            {items.map((elem, index) => {

              return (<div className="listItem" key={index}>
                <div className="text">
                  <h3>{elem}</h3>
                  <button className="delBtn" onClick={()=>delItem(index)}>Del</button>
                </div>
              </div>)
            })
            }

          </div>
        </div>
      </div>
    </>
  )
}

export default App
