import React, { useState } from 'react'
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import AddBoxIcon from '@mui/icons-material/AddBox';

function App() {

  const [inputValue, setInputValue] = useState('')
  const [items, setItems] = useState([])


  const addItem = () => {
    if (inputValue != '') {
      setItems([...items, inputValue]);
      setInputValue('');
    }
  }

const delItem=(id)=>{
  const updatedItems=items.filter((elem,index)=>{
    return (index != id);
  })
  setItems(updatedItems);
}

const removeAll=()=>{
  setItems([]);
}

  return (
    <>
      <div className="main">
        <div className="main1">
          <div className="main11">
            <h1 className="heading">ToDo List</h1>
            <div className="inputBar">
              <input type="text" placeholder='Add Item' className='input' onChange={(e) => { setInputValue(e.target.value) }} value={inputValue} />
              <Button variant="contained" className="btn" onClick={addItem}>+</Button>
            </div>

            {/* <Fab color="primary" aria-label="add">
        <AddIcon />
      </Fab> */}

<AddBoxIcon variant="contained"></AddBoxIcon>
            {items.map((elem, index) => {

              return (<div className="listItem" key={index}>
                <div className="text">
                  <h3>{elem}</h3>
                  <button className="delBtn" onClick={()=>delItem(index)}>Del</button>
                </div>
              </div>)
            })
            }

            <button className="btn" onClick={removeAll}>Remove all</button>

          </div>
        </div>
      </div>
    </>
  )
}

export default App
